int e=8, d=7, c=6, b=5, a=4, f=3, g=2; //Ledlerin bağlı olduğu pinler.
void setup()
{
  pinMode(a, OUTPUT);
  pinMode(b, OUTPUT);
  pinMode(c, OUTPUT);
  pinMode(d, OUTPUT);   //LED PİNLERİ ÇIKIŞ OLARAK TANIMLANDI
  pinMode(e, OUTPUT);
  pinMode(f, OUTPUT);
  pinMode(g, OUTPUT);
}
void loop(){
  int i;
  for(i=0; i<10; i++){   //For döngüsünün kaç kere tekrarlanacağı belirtilir. (9 kere)
    RakamGoster(i);
    delay(1000);    //Sayırların geçiş süreleri (1 sn)
  }
}
void RakamGoster(int rakam){   //Değişken içerisindeki , i değeri neyse o case bloğu çalışır.
  switch(rakam){
    case 0 :   // 0 SAYISINI YAZDIRIR . İ değeri 0 ise ekrana 0 yazılır 
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,LOW);
      digitalWrite(g,HIGH);
      break;     //Case bloğunu sonlandırır.
    case 1:     // 1 SAYISINI YAZDIRIR
      digitalWrite(a,HIGH);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,HIGH);
      digitalWrite(e,HIGH);
      digitalWrite(f,HIGH);
      digitalWrite(g,HIGH);
      break;
    case 2:     //2 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,HIGH);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,HIGH);
      digitalWrite(g,LOW);
      break;
    case 3:    //3 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,HIGH);
      digitalWrite(f,HIGH);
      digitalWrite(g,LOW);
      break;
    case 4:  //4 SAYISINI YAZDIRIR
      digitalWrite(a,HIGH);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,HIGH);
      digitalWrite(e,HIGH);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 5:     //5 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,HIGH);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,HIGH);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 6:    //6 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,HIGH);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 7:    //7 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,HIGH);
      digitalWrite(e,HIGH);
      digitalWrite(f,HIGH);
      digitalWrite(g,HIGH);
      break;
    case 8:    // 8 SAYSINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 9:    //9 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,HIGH);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
  }
}

#include<Servo.h>      //Servonun çalışması için gerekli olan kütüphane 
Servo servo1;          //Servo 1 adında değişken oluşturuldu
int aci;              //Açı değerinin tutulacağı değişken
 
void setup() {
 servo1.attach(2);   //Servonun bağlı olduğu pin numarası (D2)
}
 
void loop() {
 
for(aci=0;aci<=180;aci+=3){
      //aci değeri 180 den küçük veya eşit  olduğu sürece ileri yönde dönecek servo her dönmede 3 adım 
     //büyük olursa ters yönde dönmeye başlar
servo1.write(aci);
delay(100);                         //Geri dönüş için bekleme süresi
}
for(aci=180;aci>=0;aci-=3){      //Ters yönde dönerken atacağı adım sayısı  (-3 adım her seferde)
servo1.write(aci);      
delay(100);     //ileri dönüş için 100ms bekle
}
}
