int e=8, d=7, c=6, b=5, a=4, f=3, g=2; //Ledlerin bağlı olduğu pinler.
void setup()
{
  pinMode(a, OUTPUT);
  pinMode(b, OUTPUT);
  pinMode(c, OUTPUT);
  pinMode(d, OUTPUT);   //LED PİNLERİ ÇIKIŞ OLARAK TANIMLANDI
  pinMode(e, OUTPUT);
  pinMode(f, OUTPUT);
  pinMode(g, OUTPUT);
}
void loop(){
  int i;
  for(i=0; i<10; i++){   //For döngüsünün kaç kere tekrarlanacağı belirtilir. (9 kere)
    RakamGoster(i);
    delay(1000);    //Sayırların geçiş süreleri (1 sn)
  }
}
void RakamGoster(int rakam){   //Değişken içerisindeki , i değeri neyse o case bloğu çalışır.
  switch(rakam){
    case 0 :   // 0 SAYISINI YAZDIRIR . İ değeri 0 ise ekrana 0 yazılır 
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,LOW);
      digitalWrite(g,HIGH);
      break;     //Case bloğunu sonlandırır.
    case 1:     // 1 SAYISINI YAZDIRIR
      digitalWrite(a,HIGH);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,HIGH);
      digitalWrite(e,HIGH);
      digitalWrite(f,HIGH);
      digitalWrite(g,HIGH);
      break;
    case 2:     //2 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,HIGH);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,HIGH);
      digitalWrite(g,LOW);
      break;
    case 3:    //3 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,HIGH);
      digitalWrite(f,HIGH);
      digitalWrite(g,LOW);
      break;
    case 4:  //4 SAYISINI YAZDIRIR
      digitalWrite(a,HIGH);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,HIGH);
      digitalWrite(e,HIGH);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 5:     //5 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,HIGH);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,HIGH);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 6:    //6 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,HIGH);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 7:    //7 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,HIGH);
      digitalWrite(e,HIGH);
      digitalWrite(f,HIGH);
      digitalWrite(g,HIGH);
      break;
    case 8:    // 8 SAYSINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,LOW);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
    case 9:    //9 SAYISINI YAZDIRIR
      digitalWrite(a,LOW);
      digitalWrite(b,LOW);
      digitalWrite(c,LOW);
      digitalWrite(d,LOW);
      digitalWrite(e,HIGH);
      digitalWrite(f,LOW);
      digitalWrite(g,LOW);
      break;
  }
}

#include<Servo.h>      //Servonun çalışması için gerekli olan kütüphane 
Servo servo1;          //Servo 1 adında değişken oluşturuldu
int aci;              //Açı değerinin tutulacağı değişken
 
void setup() {
 servo1.attach(2);   //Servonun bağlı olduğu pin numarası (D2)
}
 
void loop() {
 
for(aci=0;aci<=180;aci+=3){
      //aci değeri 180 den küçük veya eşit  olduğu sürece ileri yönde dönecek servo her dönmede 3 adım 
     //büyük olursa ters yönde dönmeye başlar
servo1.write(aci);
delay(100);                         //Geri dönüş için bekleme süresi
}
for(aci=180;aci>=0;aci-=3){      //Ters yönde dönerken atacağı adım sayısı  (-3 adım her seferde)
servo1.write(aci);      
delay(100);     //ileri dönüş için 100ms bekle
}
}

void blink(int LEDPin, int LEDOnTime, int LEDOffTime) // LED yakıp sönme fonksiyonu
{
  digitalWrite(LEDPin, HIGH);
  delay(LEDOnTime);
  digitalWrite(LEDPin, LOW);
  delay(LEDOffTime);
}

void setup(){
  for(int i=2; i<7; i++){ pinMode(i, OUTPUT);} //2-7 arasında çalışıcak for döngüsü başlatılır	
  Serial.begin(9600);    //seri haberleşme başlatılır
}

void loop(){
  if (Serial.available()) {

    
    char ser = Serial.read();   //Serial monitörden okuma yapıyoruz 
    
    switch (ser) {        //Seri monitörden okunan değer hangi case numarası ile eşleşirse o case bloğu çalışır.
      case '1':          //Seri monitörden 1 yazılırsa bu case değeri ile eşleşir ve bu blok çalışır
      digitalWrite(2 , HIGH);
      digitalWrite(3 , LOW);
      digitalWrite(4 , LOW);
      digitalWrite(5 , LOW);
      digitalWrite(6 , LOW);
      delay(500);         //bekleme süresi
        break;           //case bloğunu sonlandırır.
        
      case '2':
        digitalWrite(2 , HIGH);
        digitalWrite(3 , HIGH);
        digitalWrite(4 , LOW);
        digitalWrite(5 , LOW);
        digitalWrite(6 , LOW);
        break;
        
      case '3':
        digitalWrite(2 , HIGH);
        digitalWrite(3 , HIGH);
        digitalWrite(4 , HIGH);
        digitalWrite(5 , LOW);
        digitalWrite(6 , LOW);
           
        break;
      case '4':
        digitalWrite(2 , HIGH);
        digitalWrite(3 , HIGH);
        digitalWrite(4 , HIGH);
        digitalWrite(5 , HIGH);
        digitalWrite(6 , LOW);
        break;
      case '5':
        digitalWrite(2 , HIGH);
        digitalWrite(3 , HIGH);
        digitalWrite(4 , HIGH);
        digitalWrite(5 , HIGH);
        digitalWrite(6 , HIGH);
        break;
    } 
  }
}
