{
// 3. kez basılınca yapılacaklar yazılabilir.

digitalWrite(led1, LOW);
digitalWrite(led3, LOW);
deger=5;
delay(10);
}

//Buton bırakıldı ve deger değişkeni 5 ise
if ( (butondurumu == LOW) && (deger == 5) )

